cmd:
while true; do
    if [ ! -f /cvmfs/cms.cern.ch/cmsset_default.sh ]; then
        echo "Re mount CVMFS"
        umount /cvmfs/cms.cern.ch ; mount -t cvmfs cms.cern.ch /cvmfs/cms.cern.ch/
    fi;
    sleep 10;
    if [ ! -f /cvmfs/cms.cern.ch/cmsset_default.sh ]; then
        echo "Failed mounting CVMFS...trying again"
    fi;
    sleep 5;
done;
